import React, { useState, useEffect } from 'react';
import axios from 'axios';

function RiesgoList() {
  const [riesgos, setRiesgos] = useState([]);

  useEffect(() => {
    axios.get('/api/riesgos')
      .then(response => {
        setRiesgos(response.data.riesgos);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleEliminarRiesgo = (id) => {
    axios.delete(`/api/riesgos/${id}`)
      .then(response => {
        setRiesgos(riesgos.filter(riesgo => riesgo.id !== id));
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleModificarRiesgo = (id) => {
    window.location.href = '/ActualizarRiesgo';
    console.log(`Modificar riesgo con ID: ${id}`);
  };

  return (
    <div className="container mt-4">
      <h2>Listado de Riesgos</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Nombre</th>
            <th scope="col">Descripción</th>
            <th scope="col">Usuario</th>
            <th scope="col">País</th>
            <th scope="col">Acciones</th>
          </tr>
        </thead>
        <tbody>
          {riesgos.map(riesgo => (
            <tr key={riesgo.id}>
              <th scope="row">{riesgo.id}</th>
              <td>{riesgo.nombre}</td>
              <td>{riesgo.descripcion}</td>
              <td>{riesgo.usuario}</td>
              <td>{riesgo.pais}</td>
              <td>
                <button className="btn btn-primary" onClick={() => handleModificarRiesgo(riesgo.id)}>Modificar</button>
                <button className="btn btn-danger" onClick={() => handleEliminarRiesgo(riesgo.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default RiesgoList;
