import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ActualizarRiesgo from './ActualizarRiesgo'; // Importa el componente de actualización

function RiesgoList() {
  const [riesgos, setRiesgos] = useState([]);
  const [riesgoSeleccionado, setRiesgoSeleccionado] = useState(null);

  useEffect(() => {
    axios.get('/api/riesgos')
      .then(response => {
        setRiesgos(response.data.riesgos);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleEliminarRiesgo = (id) => {
    axios.delete(`/api/riesgos/${id}`)
      .then(response => {
        setRiesgos(riesgos.filter(riesgo => riesgo.id !== id));
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleModificarRiesgo = (riesgo) => {
    setRiesgoSeleccionado(riesgo);
  };

  return (
    <div className="container mt-4">
      <h2>Listado de Riesgos</h2>
      <table className="table">
        {/* ... (código de la tabla) */}
        <tbody>
          {riesgos.map(riesgo => (
            <tr key={riesgo.id}>
              {/* ... (celdas de la tabla) */}
              <td>
                <button className="btn btn-primary" onClick={() => handleModificarRiesgo(riesgo)}>Modificar</button>
                <button className="btn btn-danger" onClick={() => handleEliminarRiesgo(riesgo.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {riesgoSeleccionado && <ActualizarRiesgo riesgo={riesgoSeleccionado} />} {/* Muestra ActualizarRiesgo si hay un riesgo seleccionado */}
    </div>
  );
}

export default RiesgoList;
