import React from 'react';
import axios from 'axios';

function Menu({ onMenuClick }) {

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const token = localStorage.getItem('token');
    console.log('token react: ' + token)
    if (!token) {
      // Si el usuario no está autenticado, redirigir a la página de inicio de sesión
      window.location.href = '/'; // Cambia la URL según tu configuración
      return;
    }

    console.log('cerrar sesion')
    axios.post('/api/logout', {
      mode: 'cors'
    })
    .then(response => {
      const token = response.data.token;
      console.log(token)
      // Guardar el token en el localStorage
      localStorage.setItem('token', token);

      // Redireccionar a la página de dashboard
      window.location.href = '/';
    })
    .catch(error => {

      console.error('Error al cerrar sesión:', error);
    });
  };

  return (

    <div>
      <div className= "form-label">
      <div className="btn-group">
      <button className="btn btn-outline-primary" onClick={() => onMenuClick('crear')}>Crear Riesgo</button>
      <button className="btn btn-outline-success" onClick={() => onMenuClick('ver')}>Ver Riesgos</button>
      <button className="btn btn-outline-success" onClick={() => onMenuClick('crearUsuario')}>Crear Usuario</button>
      <button className="btn btn-outline-success" onClick={() => handleSubmit}>Cerrar Sesión</button>
    </div>
    </div>
    </div>
  );
}

export default Menu;
