import React from 'react';

function Menu({ onMenuClick }) {

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('/api/logout', {
      username: username,
      password: password
    }, {
      mode: 'cors'
    })
    .then(response => {
      const token = response.data.token;
      console.log(token)
      // Guardar el token en el localStorage
      localStorage.setItem('token', token);

      // Redireccionar a la p치gina de dashboard
      window.location.href = '/dashboard';
    })
    .catch(error => {

      console.error('Error al iniciar sesi칩n:', error);
      setShowAlert(true);
    });
  };


  return (

    <div>
      <div className= "form-label">
      <div className="btn-group">
      <button className="btn btn-outline-primary" onClick={() => onMenuClick('crear')}>Crear Riesgo</button>
      <button className="btn btn-outline-success" onClick={() => onMenuClick('ver')}>Ver Riesgos</button>
      <button className="btn btn-outline-success" onClick={() => onMenuClick('salir')}>Ver Riesgos</button>
    </div>
    </div>
    </div>
  );
}

export default Menu;
