import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('/api/login', {
      username: username,
      password: password
    }, {
      mode: 'cors' // Esta configuración debe ir aquí
    })
    .then(response => {
      console.log(response.data);

      // Redireccionar a la página de dashboard
      window.location.href = '/dashboard';
    })
    .catch(error => {
      console.error('Error al iniciar sesión:', error);
    });
  };

  return (
    <div class="dropdown-menu">
      <h2>Login</h2>
      <form class="px-4 py-3" onSubmit={handleSubmit}>
      <div class="form-group">
      <input
      class="form-control"
        type="text"
        placeholder="Usuario"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        name="username" // Asegúrate de tener esto en tu campo de nombre de usuario
        required
      /><br />
      </div>
      <div class="form-group">
      <input
        class="form-control"
        type="password"
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        name="password" // Asegúrate de tener esto en tu campo de contraseña
        required
      />
      <br />
      </div>
        <button type="submit" class="btn btn-primary">Iniciar Sesión</button>
      </form>
    </div>
  );





}

export default Login;
