import React, { useState } from 'react';
import { withRouter  } from 'react-router-dom';
import axios from 'axios';

function CrearRiesgo() {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [pais, setPais] = useState('');
  const history = useHistory(); // Obtener el historial de navegación

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!userIsAuthenticated()) {
      // Si el usuario no está autenticado, redirigir a la página de inicio de sesión
      history.push('/login'); // Cambia la URL según tu configuración
      return;
    }

    axios.post('/api/riesgos', {
      nombre: nombre,
      descripcion: descripcion,
      pais: pais
    })
    .then(response => {
      console.log(response.data.mensaje);
      // Limpia los campos después de crear el riesgo
      setNombre('');
      setDescripcion('');
      setPais('');
    })
    .catch(error => {
      console.error('Error al crear el riesgo:', error);
    });
  };

  return (
    <div className="container mt-4">
      <h2>Crear Riesgo</h2>
      <form onSubmit={handleSubmit}>
        < div className="mb-3">
          <label htmlFor="nombre" className="form-label">Nombre:</label>
          <input
            type="text"
            id="nombre"
            className="form-control"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="descripcion" className="form-label" >Descripción:</label>
          <input
            type="text"
            id="descripcion"
            className="form-control"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="pais" className="form-label">País:</label>
          <select className="form-select"
            id="pais"
            value={pais}
            onChange={(e) => setPais(e.target.value)}
          >
            <option value="">Selecciona un país</option>
            {paises.map((nombrePais, index) => (
              <option key={index} value={nombrePais}>
                {nombrePais}
              </option>
            ))}
          </select>
        </div>
        <button className="btn btn-primary" type="submit">Crear Riesgo</button>
      </form>
    </div>
  );
}

export default CrearRiesgo;
