import React, { useState } from 'react';
import CrearRiesgo from './CrearRiesgo';
import RiesgoList from './RiesgoList';
import EliminarRiesgo from './EliminarRiesgo';
import ActualizarRiesgo from './ActualizarRiesgo';
import Menu from './Menu';

function Dashboard() {
  const [opcionMenu, setOpcionMenu] = useState(null);

  const handleMenuClick = (opcion) => {
    setOpcionMenu(opcion);
  };

  let contenido;

  switch (opcionMenu) {
    case 'crear':
      contenido = <CrearRiesgo onRiesgoCreado={nuevoRiesgo => {}} />;
      break;
    case 'eliminar':
      // Agrega el componente para eliminar riesgos aquí
      break;
    case 'modificar':
      contenido = <ActualizarRiesgo onRiesgoActualizado={nuevoRiesgo => {}} />;
      break;
    case 'ver':
      contenido = <RiesgoList />;
      break;
    default:
      contenido = null;
  }

  return (
    <div className="App">
      <div  class="container">
      <h1 class="display-2">Administración de Riesgos</h1>
      <Menu onMenuClick={handleMenuClick} />
      {contenido}
      </div>
    </div>
  );
}

export default Dashboard;
