import React, { useState, useEffect } from 'react';
import axios from 'axios';

function RiesgoList() {
  const [riesgos, setRiesgos] = useState([]);

  useEffect(() => {
    axios.get('/api/riesgos')
      .then(response => {
        setRiesgos(response.data.riesgos);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const handleEliminarRiesgo = (id) => {
    axios.delete(`/api/riesgos/${id}`)
      .then(response => {
        setRiesgos(riesgos.filter(riesgo => riesgo.id !== id));
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleModificarRiesgo = (id) => {
    // Aquí puedes implementar la lógica para modificar un riesgo
    console.log(`Modificar riesgo con ID: ${id}`);
  };

  return (
    <div>
      <h2>Listado de Riesgos</h2>
      <ul>
        {riesgos.map(riesgo => (
          <li key={riesgo.id}>
            {riesgo.nombre} {riesgo.descripcion} {riesgo.usuario} {riesgo.pais}
            <button onClick={() => handleModificarRiesgo(riesgo.id)}>Modificar</button>
            <button onClick={() => handleEliminarRiesgo(riesgo.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default RiesgoList;
