import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CrearUsuario() {
  const [nombre, setNombre] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Obtener el token de localStorage si está disponible
    const token = localStorage.getItem('token');
    console.log('token react: ' + token)
    if (!token) {
      // Si el usuario no está autenticado, redirigir a la página de inicio de sesión
      window.location.href = '/'; // Cambia la URL según tu configuración
      return;
    }
    const config = {
        headers: {
          'Accept': '*/*',
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
    };

    axios.post('/api/crearusuario', {
      nombre: nombre,
      password: password
    }, config
    )
    .then(response => {
      console.log(response.data.mensaje);
      // Limpia los campos después de crear el riesgo
      setNombre('');
      setPassword('');

    })
    .catch(error => {
      console.error('Error al crear el usuario:', error);
    });
  };

  return (
    <div className="container mt-4">
      <h2>Crear Riesgo</h2>
      <form onSubmit={handleSubmit}>
        < div className="mb-3">
          <label htmlFor="nombre" className="form-label">Nombre:</label>
          <input
            type="text"
            id="nombre"
            className="form-control"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="descripcion" className="form-label" >Descripción:</label>
          <input
            type="text"
            id="descripcion"
            className="form-control"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="Proveedor" className="form-label" >Proveedor:</label>
          <input
            type="text"
            id="descripcion"
            className="form-control"
            value={proveedor}
            onChange={(e) => setProveedor(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="descripcion" className="form-label" >Impacto:</label>
          <input
            type="text"
            id="Impacto"
            className="form-control"
            value={impacto}
            onChange={(e) => setImpacto(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="descripcion" className="form-label" >Probabilidad:</label>
          <input
            type="text"
            id="Probabilidad"
            className="form-control"
            value={probabilidad}
            onChange={(e) => setProbabilidad(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="pais" className="form-label">País:</label>
          <select className="form-select"
            id="pais"
            value={pais}
            onChange={(e) => setPais(e.target.value)}
          >
            <option value="">Selecciona un país</option>
            {paises.map((nombrePais, index) => (
              <option key={index} value={nombrePais}>
                {nombrePais}
              </option>
            ))}
          </select>
        </div>
        <button className="btn btn-primary" type="submit">Crear Riesgo</button>
      </form>
    </div>
  );
}

export default CrearRiesgo;
