import React, { useState } from 'react';
import axios from 'axios';

function ActualizarRiesgo({ id, onRiesgoActualizado }) {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');

  const handleActualizar = (e) => {
    e.preventDefault();

    axios.put(`/api/riesgos/${id}`, { nombre, descripcion })
      .then(response => {
        console.log(response.data.mensaje);
        onRiesgoActualizado(id, nombre, descripcion);
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Actualizar Riesgo</h2>
      <form onSubmit={handleActualizar}>
        <div>
          <label htmlFor="nombre">Nombre:</label>
          <input
            type="text"
            id="nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="descripcion">Descripci√≥n:</label>
          <input
            type="text"
            id="descripcion"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
        </div>
        <button type="submit">Actualizar Riesgo</button>
      </form>
    </div>
  );
}

export default ActualizarRiesgo;
